import React, { useEffect, useState } from "react";
import CryptoJS from "crypto-js";
import axios from "axios";

const App = () => {
  const [inputText, setInputText] = useState("");
  const [encryptedText, setEncryptedText] = useState("");

  const encryptData = async (data) => {
    const key = "secret_key"; // Replace with your own secret key
    setEncryptedText(CryptoJS.AES.encrypt(data, key).toString());
  };
  const callBackend = async () => {
    await axios.post("http://localhost:5000/encryptdata", {
      encryptedText: encryptedText,
    });
  };
  useEffect(() => {
    callBackend();
  }, [inputText]);
  return (
    <div>
      <input
        type="text"
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
      />
      <button onClick={() => encryptData(inputText)}>Encrypt</button>
      <div>encryptedText :{encryptedText}</div>
    </div>
  );
};

export default App;
