// import crypto from 'crypto-js';
import forge from 'node-forge';
import React, { useState } from 'react';


function App() {

 const [inputData, setInputData] = useState('');
  const [encryptedData, setEncryptedData] = useState('');

  const handleInputChange = (e) => {
    setInputData(e.target.value);
  };

  const handleEncrypt = () => {
    // Step 1: Generate Diffie-Hellman parameters
    const dh = forge.diffieHellman.create();

    dh.generateKeys(256);
    const publicKey = dh.getPublicKey();

    // Step 2: Exchange public keys
    // Simulating the other party's public key received
    const otherPartyPublicKey = '<other_party_public_key>';

    // Step 3: Compute the shared secret key
    const otherPublicKey = forge.util.createBuffer(forge.util.decode64(otherPartyPublicKey));
    const sharedSecret = dh.computeSecret(otherPublicKey);

    // Step 4: Encrypt the input data
    const cipher = forge.cipher.createCipher('AES-CBC', sharedSecret);
    cipher.start({ iv: forge.random.getBytesSync(16) });
    cipher.update(forge.util.createBuffer(inputData));
    cipher.finish();
    const encrypted = cipher.output.toHex();

    setEncryptedData(encrypted);
  };

  return (
    <div>
      <input type="text" value={inputData} onChange={handleInputChange} />
      <button onClick={handleEncrypt}>Encrypt</button>
      <div>Encrypted Data: {encryptedData}</div>
    </div>
  );
}

export default App;
