import React, { useState } from 'react';


function App() {

  const [inputData, setInputData] = useState('');
  const [encryptedData, setEncryptedData] = useState('');

  const handleInputChange = (e) => {
    setInputData(e.target.value);
  };

  const handleEncrypt = () => {
    // Step 1: Generate Diffie-Hellman parameters
    const dh = crypto.diffieHellman(256);
    const publicKey = dh.generateKeys('base64');

    // Step 2: Exchange public keys
    // Simulating the other party's public key received
    const otherPartyPublicKey = '<other_party_public_key>';

    // Step 3: Compute the shared secret key
    const sharedSecret = dh.computeSecret(otherPartyPublicKey, 'base64', 'hex');

    // Step 4: Encrypt the input data
    const encrypted = crypto.AES.encrypt(inputData, sharedSecret).toString();

